#Functiton to seperate each line into name, bank etc.
def format_lines(dat, seperator='~'):
    raw_lines = dat.split('~')
    formatted_lines = []
    for line in raw_lines:
        words = line.split(' ')
        new_line = []
        new_line.append( ' '.join(words[ : 2]) )
        new_line.append( ' '.join(words[2:-5]) )
        new_line.extend( words[-5:] )
        formatted_lines.append(new_line)
    return formatted_lines

#Function to store the formatted lines to the excel file.    
def store_to_xl(formatted_lines):
    book_ro = open_workbook("D:\Python\data.xls") #change the path to your xl file
    book = copy(book_ro)
    sheet = book.get_sheet(0)
    row = book_ro.sheet_by_index(0).nrows
    for line in formatted_lines:
        for i in range(7):
            sheet.write(row, i, line[i])
        row += 1
    book.save("D:\Python\data.xls") #change the path here too


lines = format_lines(str_dat)
